                    }}
                    // value=""
                    className={inputborder.root}
                    InputLabelProps={{
                      style: {
                        color: styles.app_color,
                        fontFamily: "Poppins",
                      },
                    }}
                    InputProps={{
                      readOnly: true,
                      startAdornment: (
                        <InputAdornment position="start">
                          <FmdGoodSharpIcon sx={{ color: styles.app_color }} />
                        </InputAdornment>
                      ),
                    }}
                  />
                </Grid>
                <Grid item md={4} width={"100%"}>
                  <LocalizationProvider dateAdapter={AdapterDateFns}>
                    <MobileDatePicker
                      inputFormat="dd/MM/yyyy"
                      disabled={disableCalendar}
                      sx={{
                        "& .MuiPickersToolbar-penIconButton": {
                          display: "none",
                        },
                      }}
                      closeOnSelect
                      disablePast
                      inputRef={busDateSelectRef}
                      label="Date"
                      value={searchDate}
                      onChange={(newValue) => {
                        setSearchDate(newValue);
                      }}
                      renderInput={(params) => (
                        <TextField
                          id="dateInputTag"
                          sx={{ minWidth: "90px" }}
                          // size="small"
                          {...params}
                          fullWidth
                          autoComplete="off"
                          size="small"
                          label={
                            <span style={{ paddingRight: "0.5rem" }}>Date</span>
                          }
                          // value=""
                          className={`${inputborder.root}`}
                          InputLabelProps={{
                            style: {
                              color: styles.app_color,
                              fontFamily: "Poppins",
                            },
                          }}
                          InputProps={{
                            readOnly: true,
                            startAdornment: (
                              <InputAdornment position="start">
                                {/* <CalendarMonthIcon sx={{color:'#003556'}}/> */}
                                <img
                                  src={calendermonth}
                                  alt="caleder"
                                  width="27"
                                  height="100"
                                />
                              </InputAdornment>
                            ),
                            endAdornment: (
                              <InputAdornment position="start">
                                <span
                                  className={`busesDatePickBtns ${helperFunctions.convertDateStr(
                                    searchDate
                                  ) ==
                                    helperFunctions.convertDateStr(new Date())
                                    ? "active"
                                    : ""
                                    }`}
                                  onMouseDown={handleTodayMouseDown}
                                >
                                  Today
                                </span>
                                &nbsp;
                                <span
                                  className={`busesDatePickBtns ${helperFunctions.convertDateStr(
                                    searchDate
                                  ) ==
                                    helperFunctions.convertDateStr(
                                      new Date(
                                        new Date().setDate(
                                          new Date().getDate() + 1
                                        )
                                      )
                                    )
                                    ? "active"
                                    : ""
                                    }`}
                                  onMouseDown={handleTomorrowMouseDown}
                                >
                                  Tomorrow
                                </span>
                              </InputAdornment>
                            ),
                            onClick: handleCalenderInputClick,
                          }}
                        />
                      )}
                    />
                  </LocalizationProvider>
                </Grid>
                <Grid item>
                  <img
                    src={swap}
                    alt="swapbutton"
                    className={gomainpag.swapbtnBusSearchList}
                    onClick={handleSwap}
                  />
                </Grid>
                {/* modile response button */}
                <Grid item textAlign={"center"} width={"100%"} sx={{ display: { md: "none", xs: "block" }, paddingTop: "0px !important" }}>
                  <button
                    className={HotMainpg.hotelsearchbtn}
                    onClick={handleSearchInList}
                  >
                    Search Buses{" "}
                  </button>
                </Grid>
              </Grid>
            </Grid>

          </Paper>
          {/* Hotel search button */}
          <Grid item textAlign={"center"} mt={-3.5} sx={{ display: { md: "block", xs: "none" } }}>
            <button
              className={HotMainpg.hotelsearchbtn}
              onClick={handleSearchInList}
            >
              Search Buses{" "}
            </button>
          </Grid>
        </Grid>
      </Dialog>
    </>
  );
};

export default BusesSearchList;


