      // Use html2canvas to create an image of the component for the current page
      const scrollY = i * 740;
      const canvas = await html2canvas(newElement, {
        x: 0,
        y: scrollY,
        height: 740,
        width: 780,
      });
      const imgData = canvas.toDataURL("image/png", 1.0);
