                                id="demo-simple-select"
                                value={discountType}
                                label="Discount Type"
                                onChange={handleDiscountTypeChange}
                              >
                                <MenuItem value={1}>Amount</MenuItem>
                                <MenuItem value={2}>Percentage</MenuItem>
                              </Select>
                            </FormControl>
                          </Grid>
                          {errors.discountType && (
                            <span className="goAdminErrorMessages">
                              {errors.discountType.message}
                            </span>
                          )}

                          <Grid item md={6} sm>
                            <TextField
                              inputProps={{
